import React, { Component } from 'react';
import Footer from '../include/footer';
import NextFooter from '../include/next_footer';

class Mail extends Component{
    constructor(props){
        super(props)
        this.state = {
            email: ""
        }
    }

    onChange = (e) => {
        this.setState({[e.target.name]: e.target.value});
    }

    render(){
        return(
            <div>
                <section className="mail-section">
                    <div className="container">
                        <div className="row">
                            <div className="col-md-6">
                                <div className="part-1">
                                    <p>Slack</p>
                                    <p>First, enter your email.</p>
                                    <p>Just one more mail- a quik confirmation<br/> - before you say good bye to overstuffed<br/> inboxes for good</p>
                                    <div className="form-group">
                                        <label>Your email address</label>
                                        <input 
                                        type="text"
                                        name="email" 
                                        value={this.state.email}
                                        onChange={this.onChange}
                                        className="form-control"
                                        placeholder={'Enter your email'}/>
                                    </div>
                                    <div className="form-group">
                                        <input type="check"/>
                                    </div>
                                </div>
                            </div>
                            <div className="col-md-6">
                                <div className="part-2">
                                  <p>Looking to join an existing workspace?</p>
                                  <button>Find your workspace.</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
                <NextFooter/>
                <Footer/>
            </div>
        )
    }
}

export default Mail;
