import { SEND_MAIL } from './types'
import axios from 'axios';

var api_url = process.env.REACT_APP_API_URL;


const sendMailRequest = (data) => dispatch => {
    console.log(data);
    console.log(api_url);
    return axios.post(`${api_url}/api/sendmail`, data);
}
