import React, { Component } from 'react';
import Footer from '../../include/footer';
import NextFooter from '../../include/next_footer';
import PropTypes from 'prop-types';

class MailConfirmation extends Component{
    constructor(props){
        super(props);
        this.state = {
            name1: "",
            name2: "",
            name3: "",
            name4: "",
            name5: "",
            name6: "",
        }

        onChange = (e) =>{
            this.setState({[e.target.name]: e.target.value})
        }
    }
    render(){
        return(
            <div>
                <section className="confirm_main_section">
                    <div className="container">
                        <div className="row">
                            <div className="col-md-12">
                                <div className="content">
                                    <h4>Check your email!</h4>
                                    <p>
                                        We've send 6-digits confirmation code to 
                                       <span> splendidjohnpaul@gmail.com.</span> It will expire shortly, so enter it

                                    </p>
                                    <div className="fields">
                                        <input 
                                            type="text" 
                                            name="name_1" 
                                            value={this.state.name1} 
                                            onChange={this.onChange} 
                                            className="txt_nme1"
                                        />
                                        <input 
                                            type="text" 
                                            name="name_2" 
                                            value={this.state.name2} 
                                            onChange={this.onChange} 
                                            className="txt_nme2"
                                        />
                                        <input 
                                            type="text" 
                                            name="name_3" 
                                            value={this.state.name3} 
                                            onChange={this.onChange} 
                                            className="txt_nme3"
                                        />
                                        <span>&ndash;</span>
                                        <input 
                                            type="text" 
                                            name="name_4" 
                                            value={this.state.name4} 
                                            onChange={this.onChange} 
                                            className="txt_nme4"
                                            onKeyup={this.sendConfr}
                                        />
                                        <input 
                                            type="text" 
                                            name="name_5" 
                                            value={this.state.name5} 
                                            onChange={this.onChange} 
                                            className="txt_nme5"
                                        />
                                        <input 
                                            type="text" 
                                            name="name_6" 
                                            value={this.state.name6} 
                                            onChange={this.onChange} 
                                            className="txt_nme6"
                                        />
                                    </div>
                                    <p>
                                        Keeping this window open while checking for your code.
                                        Remember to try your spam folder!.
                                    </p>

                                    <img src="images/bottom_1.png"/>
                                </div>
                            </div>
                        </div>
                    </div>
                </section> 
                <NextFooter/>
                <Footer/>
            </div>
        );
    }
}


export default MailConfirmation;
